import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Carousel.css';
import localExterior from '../assets/images/local-exterior.png';
import logoDorado from '../assets/images/logo-dorado.png';
import brillito1 from '../assets/images/brillito1.png';
import brillito2 from '../assets/images/brillito2.png';
import brillito3 from '../assets/images/brillito3.png';
import brillito4 from '../assets/images/brillito4.png';

function Carousel() {
  const navigate = useNavigate();

  const irAlMenu = () => {
    navigate('/menu');
  };

  return (
    <div className="hero-section">
      {/* LADO IZQUIERDO - IMAGEN */}
      <div className="hero-left">
        <img src={localExterior} alt="Mana Coffee Local" className="hero-imagen" />
      </div>

      {/* LADO DERECHO - CONTENIDO */}
      <div className="hero-right">
        <div className="hero-content">
          <h2 className="hero-bienvenido">BIENVENIDO A</h2>
          
          {/* ✅ CONTENEDOR DEL LOGO CON BRILLITOS */}
          <div className="hero-logo-container">
            {/* Brillito superior izquierdo */}
            <img 
              src={brillito1} 
              alt="" 
              className="brillito brillito-1"
            />
            
            {/* Brillito superior derecho */}
            <img 
              src={brillito2} 
              alt="" 
              className="brillito brillito-2"
            />
            
            {/* LOGO PRINCIPAL */}
            <img src={logoDorado} alt="Mana" className="hero-logo-grande" />
            
            {/* Brillito inferior izquierdo */}
            <img 
              src={brillito3} 
              alt="" 
              className="brillito brillito-3"
            />
            
            {/* Brillito inferior derecho */}
            <img 
              src={brillito4} 
              alt="" 
              className="brillito brillito-4"
            />
          </div>
          
          <p className="hero-restobar">RESTOBAR</p>
          
          <p className="hero-subtitle">
            El sabor de la compañía es insuperable
          </p>
          
          <button className="hero-button" onClick={irAlMenu}>
            Ver Menú
          </button>
        </div>
      </div>
    </div>
  );
}

export default Carousel;
