import React from 'react';
import './Menu.css';

function Menu() {
  const menuItems = [
    { nombre: '‚òï Caf√© Americano', precio: '$5.000', descripcion: 'Caf√© negro cl√°sico' },
    { nombre: 'ü•§ Cappuccino', precio: '$6.000', descripcion: 'Con espuma de leche' },
    { nombre: 'üçµ Latte', precio: '$6.500', descripcion: 'Suave y cremoso' },
    { nombre: '‚ö° Espresso', precio: '$4.500', descripcion: 'Intenso y fuerte' },
    { nombre: 'üßä Frapp√©', precio: '$7.000', descripcion: 'Fr√≠o y refrescante' },
    ,
  ];

  return (
    <section className="menu">
      <h2 className="menu-title">Nuestro Men√∫</h2>
      <p className="menu-subtitle">Descubre nuestras deliciosas opciones</p>
      
      <div className="menu-grid">
        {menuItems.map((item, index) => (
          <div key={index} className="menu-card">
            <h3 className="menu-card-title">{item.nombre}</h3>
            <p className="menu-card-description">{item.descripcion}</p>
            <p className="menu-card-price">{item.precio}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Menu;
