// src/components/InstagramFeed.js
import React, { useState, useEffect } from 'react';
import './InstagramFeed.css';

function InstagramFeed() {
  const [lastUpdate, setLastUpdate] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // URL del 煤ltimo post (necesitas actualizar manualmente)
  // Obt茅n esta URL desde Instagram cada vez que subas un nuevo post
  const INSTAGRAM_POST_URL = 'https://www.instagram.com/p/DRu4BS3Cax3/';

  // Funci贸n para cargar el embed
  const loadInstagramEmbed = () => {
    setIsLoading(true);
    
    // Limpiar contenedor previo
    const container = document.getElementById('instagram-embed-container');
    if (container) {
      container.innerHTML = '';
    }

    // Crear nuevo script de Instagram
    const script = document.createElement('script');
    script.src = 'https://www.instagram.com/embed.js';
    script.async = true;
    
    // Crear el blockquote que Instagram convierte en embed
    const blockquote = document.createElement('blockquote');
    blockquote.className = 'instagram-media';
    blockquote.setAttribute('data-instgrm-permalink', INSTAGRAM_POST_URL);
    blockquote.setAttribute('data-instgrm-version', '14');
    
    if (container) {
      container.appendChild(blockquote);
      container.appendChild(script);
    }

    // Actualizar timestamp
    const now = new Date();
    setLastUpdate(now.toLocaleString());
    setIsLoading(false);
    
    // Guardar en localStorage cu谩ndo se actualiz贸
    localStorage.setItem('instagramLastUpdate', now.getTime());
  };

  // Verificar si necesita actualizaci贸n (una vez al d铆a)
  const checkForUpdate = () => {
    const lastUpdateTime = localStorage.getItem('instagramLastUpdate');
    const now = new Date().getTime();
    const oneDay = 24 * 60 * 60 * 1000; // 24 horas en milisegundos
    
    // Si nunca se actualiz贸 o pas贸 m谩s de un d铆a, actualizar
    if (!lastUpdateTime || (now - lastUpdateTime) > oneDay) {
      loadInstagramEmbed();
    } else {
      // Cargar embed existente
      const storedEmbed = localStorage.getItem('instagramEmbedHTML');
      if (storedEmbed && document.getElementById('instagram-embed-container')) {
        document.getElementById('instagram-embed-container').innerHTML = storedEmbed;
      }
    }
  };

  useEffect(() => {
    // Cargar al montar el componente
    checkForUpdate();
    
    // Configurar intervalo para verificar una vez al d铆a
    const interval = setInterval(checkForUpdate, 24 * 60 * 60 * 1000);
    
    return () => clearInterval(interval);
  }, []);

  // Funci贸n para actualizar manualmente
  const handleManualUpdate = () => {
    loadInstagramEmbed();
  };

  return (
    <section className="instagram-section">
      <div className="instagram-container">
        <div className="instagram-header">
          <h2 className="instagram-title"> S铆guenos en Instagram</h2>
          <button 
            onClick={handleManualUpdate}
            className="instagram-update-btn"
            disabled={isLoading}
          >
            {isLoading ? 'Actualizando...' : ' Actualizar'}
          </button>
        </div>
        
        <p className="instagram-account">@mana_coffee_pam</p>
        
        <div id="instagram-embed-container" className="instagram-embed">
          {/* Instagram embed se carga aqu铆 */}
        </div>
        
        {lastUpdate && (
          <p className="instagram-update-time">
            ltima actualizaci贸n: {lastUpdate}
          </p>
        )}
        
        <a 
          href="https://www.instagram.com/mana_coffee_pam/"
          target="_blank"
          rel="noopener noreferrer"
          className="instagram-profile-link"
        >
          Ver perfil completo en Instagram 
        </a>
      </div>
    </section>
  );
}

export default InstagramFeed;
