import React, { useState } from 'react';
import './FormularioReserva.css';

function FormularioReserva({ cerrarFormulario }) {
  // ============================================
  // ESTADO DEL FORMULARIO
  // Guarda toda la información que el usuario ingresa
  // ============================================
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    telefono: '',
    numPersonas: '',
    fecha: '',
    hora: '',
    comentarios: ''
  });

  // ============================================
  // ESTADO DE ERRORES
  // Guarda los mensajes de error de cada campo
  // ============================================
  const [errors, setErrors] = useState({});

  // ============================================
  // ESTADO DE ENVÍO
  // Controla si se está enviando o si fue exitoso
  // ============================================
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);

  // ============================================
  // FUNCIÓN: Manejar cambios en los inputs
  // Se ejecuta cada vez que escribes en un campo
  // ============================================
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
    // Limpiar error del campo cuando el usuario empieza a escribir
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  // ============================================
  // FUNCIÓN: Validar el formulario
  // Verifica que todos los campos estén correctos
  // ============================================
  const validarFormulario = () => {
    const newErrors = {};

    // Validar nombre
    if (!formData.nombre.trim()) {
      newErrors.nombre = 'El nombre es obligatorio';
    } else if (formData.nombre.trim().length < 3) {
      newErrors.nombre = 'El nombre debe tener al menos 3 caracteres';
    }

    // Validar email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      newErrors.email = 'El correo electrónico es obligatorio';
    } else if (!emailRegex.test(formData.email)) {
      newErrors.email = 'Ingresa un correo electrónico válido';
    }

    // Validar teléfono
    const telefonoRegex = /^[0-9]{10}$/;
    if (!formData.telefono.trim()) {
      newErrors.telefono = 'El teléfono es obligatorio';
    } else if (!telefonoRegex.test(formData.telefono.replace(/\s/g, ''))) {
      newErrors.telefono = 'Ingresa un teléfono válido de 10 dígitos';
    }

    // Validar número de personas
    if (!formData.numPersonas) {
      newErrors.numPersonas = 'Selecciona el número de personas';
    } else if (parseInt(formData.numPersonas) > 42) {
      newErrors.numPersonas = 'La capacidad máxima es de 42 personas';
    }

    // Validar fecha
    if (!formData.fecha) {
      newErrors.fecha = 'Selecciona una fecha';
    } else {
      const fechaSeleccionada = new Date(formData.fecha);
      const hoy = new Date();
      hoy.setHours(0, 0, 0, 0);
      if (fechaSeleccionada < hoy) {
        newErrors.fecha = 'La fecha no puede ser anterior a hoy';
      }
    }

    // Validar hora
    if (!formData.hora) {
      newErrors.hora = 'Selecciona una hora';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // ============================================
  // FUNCIÓN: Enviar el formulario
  // Se ejecuta cuando haces clic en "Confirmar Reserva"
  // AQUÍ PUEDES AGREGAR LA LÓGICA PARA ENVIAR A UN BACKEND
  // ============================================
  const handleSubmit = (e) => {
    e.preventDefault();

    // Validar antes de enviar
    if (!validarFormulario()) {
      return;
    }

    setIsSubmitting(true);

    // Simular envío al servidor (aquí puedes hacer tu petición real)
    setTimeout(() => {
      console.log('Datos de la reserva:', formData);
      
      // AQUÍ PUEDES AGREGAR:
      // - Petición fetch() o axios a tu backend
      // - Envío de email
      // - Guardado en base de datos
      // Ejemplo:
      // fetch('/api/reservas', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(formData)
      // })
      
      setIsSubmitting(false);
      setSubmitSuccess(true);

      // Cerrar el modal después de 3 segundos
      setTimeout(() => {
        cerrarFormulario();
      }, 3000);
    }, 2000);
  };

  // ============================================
  // FUNCIÓN: Cerrar el modal al hacer clic fuera
  // ============================================
  const handleOverlayClick = (e) => {
    if (e.target.className === 'modal-overlay') {
      cerrarFormulario();
    }
  };

  // ============================================
  // RENDERIZADO DEL COMPONENTE
  // ============================================
  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="modal-container">
        
        {/* ENCABEZADO DEL MODAL */}
        <div className="formulario-header">
          <button className="btn-cerrar-modal" onClick={cerrarFormulario}>
            ✕
          </button>
          <h2>Completa tu Reserva</h2>
          <p>Llena los siguientes datos para confirmar tu reserva</p>
        </div>

        {/* SI LA RESERVA FUE EXITOSA, MOSTRAR MENSAJE */}
        {submitSuccess ? (
          <div className="formulario-body">
            <div className="success-message">
              <h3>¡Reserva Confirmada! ✓</h3>
              <p>Hemos recibido tu reserva correctamente.</p>
              <p>Te enviaremos un correo de confirmación a <strong>{formData.email}</strong></p>
              <p style={{ marginTop: '20px', fontSize: '14px', color: '#666' }}>
                Este mensaje se cerrará automáticamente...
              </p>
            </div>
          </div>
        ) : (
          <>
            {/* FORMULARIO */}
            <form onSubmit={handleSubmit}>
              <div className="formulario-body">
                
                {/* MENSAJE DE CARGA */}
                {isSubmitting && (
                  <div className="loading-message">
                    Procesando tu reserva...
                  </div>
                )}

                {/* CAMPO: NOMBRE COMPLETO */}
                <div className="form-group">
                  <label htmlFor="nombre">
                    Nombre Completo <span>*</span>
                  </label>
                  <input
                    type="text"
                    id="nombre"
                    name="nombre"
                    className="form-input"
                    placeholder="Ej: Juan Pérez"
                    value={formData.nombre}
                    onChange={handleChange}
                  />
                  {errors.nombre && (
                    <span className="error-message">{errors.nombre}</span>
                  )}
                </div>

                {/* FILA DE DOS COLUMNAS: EMAIL Y TELÉFONO */}
                <div className="form-row">
                  {/* CAMPO: EMAIL */}
                  <div className="form-group">
                    <label htmlFor="email">
                      Correo Electrónico <span>*</span>
                    </label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      className="form-input"
                      placeholder="tu@email.com"
                      value={formData.email}
                      onChange={handleChange}
                    />
                    {errors.email && (
                      <span className="error-message">{errors.email}</span>
                    )}
                  </div>

                  {/* CAMPO: TELÉFONO */}
                  <div className="form-group">
                    <label htmlFor="telefono">
                      Teléfono <span>*</span>
                    </label>
                    <input
                      type="tel"
                      id="telefono"
                      name="telefono"
                      className="form-input"
                      placeholder="3001234567"
                      value={formData.telefono}
                      onChange={handleChange}
                    />
                    {errors.telefono && (
                      <span className="error-message">{errors.telefono}</span>
                    )}
                  </div>
                </div>

                {/* CAMPO: NÚMERO DE PERSONAS */}
                <div className="form-group">
                  <label htmlFor="numPersonas">
                    Número de Personas <span>*</span>
                  </label>
                  <select
                    id="numPersonas"
                    name="numPersonas"
                    className="form-select"
                    value={formData.numPersonas}
                    onChange={handleChange}
                  >
                    <option value="">Selecciona...</option>
                    <option value="1">1 persona</option>
                    <option value="2">2 personas</option>
                    <option value="3">3 personas</option>
                    <option value="4">4 personas</option>
                    <option value="5">5 personas</option>
                    <option value="6">6 personas</option>
                    <option value="7">7 personas</option>
                    <option value="8">8 personas</option>
                    <option value="9">9 personas</option>
                    <option value="10">10 personas</option>
                    <option value="15">15 personas</option>
                    <option value="20">20 personas</option>
                    <option value="25">25 personas</option>
                    <option value="30">30 personas</option>
                    <option value="35">35 personas</option>
                    <option value="42">42 personas (máximo)</option>
                  </select>
                  {errors.numPersonas && (
                    <span className="error-message">{errors.numPersonas}</span>
                  )}
                </div>

                {/* FILA DE DOS COLUMNAS: FECHA Y HORA */}
                <div className="form-row">
                  {/* CAMPO: FECHA */}
                  <div className="form-group">
                    <label htmlFor="fecha">
                      Fecha <span>*</span>
                    </label>
                    <input
                      type="date"
                      id="fecha"
                      name="fecha"
                      className="form-input"
                      value={formData.fecha}
                      onChange={handleChange}
                      min={new Date().toISOString().split('T')[0]}
                    />
                    {errors.fecha && (
                      <span className="error-message">{errors.fecha}</span>
                    )}
                  </div>

                  {/* CAMPO: HORA */}
                  <div className="form-group">
                    <label htmlFor="hora">
                      Hora <span>*</span>
                    </label>
                    <input
                      type="time"
                      id="hora"
                      name="hora"
                      className="form-input"
                      value={formData.hora}
                      onChange={handleChange}
                      min="08:00"
                      max="22:00"
                    />
                    {errors.hora && (
                      <span className="error-message">{errors.hora}</span>
                    )}
                  </div>
                </div>

                {/* CAMPO: COMENTARIOS ADICIONALES */}
                <div className="form-group">
                  <label htmlFor="comentarios">
                    Comentarios Adicionales (Opcional)
                  </label>
                  <textarea
                    id="comentarios"
                    name="comentarios"
                    className="form-textarea"
                    placeholder="¿Alguna solicitud especial? (Ej: celebración, ubicación preferida, alergias...)"
                    value={formData.comentarios}
                    onChange={handleChange}
                  ></textarea>
                </div>

              </div>

              {/* BOTONES DEL FORMULARIO */}
              <div className="formulario-footer">
                <button
                  type="button"
                  className="btn-cancelar"
                  onClick={cerrarFormulario}
                  disabled={isSubmitting}
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="btn-enviar"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Procesando...' : 'Confirmar Reserva'}
                </button>
              </div>
            </form>
          </>
        )}

      </div>
    </div>
  );
}

export default FormularioReserva;
